# Копирование исхолного дф
data_consumption = data[['Consumption']]
# Добавление новой колонки со вчерашними значениями (сдвиг колонки 'Consumption' на шаг назад)
data_consumption.loc[:,'Yesterday'] = data_consumption.loc[:,'Consumption'].shift()
# Добавление колонки с разностью между значениями одно и двух дневной давности
data_consumption.loc[:,'Yesterday_Diff'] = data_consumption.loc[:,'Yesterday'].diff()
# Удаление из дф значений NaN
data_consumption = data_consumption.dropna()

df =[]

def process(model):
    data_tables = []
    columns = ['r2', 'MAE', 'MSE','RMSE'] 
    index = []
    interval = pd.date_range(start='2016-01', periods=12, freq='M').strftime('%Y-%m').tolist()
    #print(interval)
    for date in interval:
      X_train = data_consumption[interval[0]: date].drop(['Consumption'], axis = 1)
      y_train = data_consumption.loc[interval[0]:date, 'Consumption']

      # Формирование тестовой части датасета
      X_test = data_consumption['2017'].drop(['Consumption'], axis = 1)
      y_test = data_consumption.loc['2017', 'Consumption']

      model = model.fit(X_train, y_train)
      # Функция .predict() выполняет прогнозирование
      # на основе тестового промежутка времени.
      y_pred = model.predict(X_test)

      # Средняя абсолютная ошибка
      mean_absolute_error = metrics.mean_absolute_error(y_test, y_pred)
      # Средняя квадратичная ошибка
      mse = metrics.mean_squared_error(y_test, y_pred) 
      # Медианная вбсолютная ошибка
      median_absolute_error = metrics.median_absolute_error(y_test, y_pred)
      # Коэффициент детерминации (R^2) - определяет степень соответствия
      # полученной в ходе МО модели исходным данным. (Модели с коэфф. детерм. 
      # более 0,8 (80%) считаются достаточно точными)
      r2 = metrics.r2_score(y_test, y_pred)
  
      index.append(interval[0] +'-'+  date)
      data_tables.append([round(r2,4), round(mean_absolute_error,4), round(mse,4), round(median_absolute_error,4)] )
 
    df = pd.DataFrame(data_tables,index,columns)
    
    return df
  


df.append(('LinearRegression',process(LinearRegression())))

df.append(('MLPRegressor(solver = lbfgs)',process(MLPRegressor(solver = 'lbfgs'))))

