# Функция генерирования дополнительных признаком для временного ряда
# средствами функции .dt, которая позволяет выделять временные
# диапазоны с различным шагом (час, день, неделя, месяц, квартал, год и т.д.)

def Create_time_predictors(df):

	df['datetime'] = df.index # Копирование индексов дф в колонку
	df['hour'] = df.datetime.dt.hour # Час
	df['dayofweek'] = df.datetime.dt.dayofweek # День недели
	df['quarter'] = df.datetime.dt.quarter # Квартал
	df['month'] = df.datetime.dt.month # Месяц
	df['dayofyear'] = df.datetime.dt.dayofyear # День года
	df['day'] = df.datetime.dt.day # День
	df['weekofyear'] = df.datetime.dt.isocalendar().week # Неделя
	df = df.drop('datetime', axis = 1) # Удаление из дф колонки
	# Операция возвращения функцией дф
	return df

import warnings
warnings.filterwarnings('ignore')

data_correction = Create_time_predictors(data_correction)
data_correction = data_correction.drop(['Consumption', 'Wind', 'Solar', 'Wind+Solar',
       'trend_residuals', 'trend_eq', 'Consumption_eq_roll_7_day', 'constant'], axis = 1)
data_correction = data_correction.dropna()


X_train = data_correction[:'2016'].drop(['Consumption_eq'], axis = 1)
y_train = data_correction.loc[:'2016', 'Consumption_eq']

X_test = data_correction['2017'].drop(['Consumption_eq'], axis = 1)
y_test = data_correction.loc['2017', 'Consumption_eq']

# Spot Check Algorithms
models = []
models.append(('LR', LinearRegression()))
models.append(('NN', MLPRegressor(solver = 'lbfgs')))  #neural network
models.append(('KNN', KNeighborsRegressor())) 
models.append(('RF', RandomForestRegressor(n_estimators = 50))) # Ensemble method - collection of many decision trees 
models.append(('SVR', SVR(gamma='auto', kernel='rbf'))) # kernel = linear
# Evaluate each model in turn
results = []
names = []
for name, model in models:
    # TimeSeries Cross validation
  tscv = TimeSeriesSplit(n_splits=10)
    
  cv_results = cross_val_score(model, X_train, y_train, cv=tscv, scoring='r2')
  results.append(cv_results)
  names.append(name)
  print('%s: %f (%f)' % (name, cv_results.mean(), cv_results.std()))
    
# Compare Algorithms
plt.boxplot(results, labels=names)
plt.title('Algorithm Comparison')
plt.show()

